package id.ac.ui.cs.advprog.subscription.model;public class ActivateState implements SubscriptionState {    private final Subscription subscription;    public ActivateState(Subscription subscription) {        this.subscription = subscription;    }    @Override    public void activateSubscription() {        subscription.setSubscriptionState(new ActivateState(subscription));    }    @Override    public void cancelSubscription() {        throw new IllegalStateException("Can't cancel an activated sub!");    }}