package id.ac.ui.cs.advprog.subscription.service;import id.ac.ui.cs.advprog.subscription.model.Subscription;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.web.client.RestTemplate;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import java.util.logging.Logger;@Servicepublic class SubscriptionServiceImpl implements SubscriptionService {    private static final Logger logger = Logger.getLogger(SubscriptionService.class.getName());    private final RestTemplate restTemplate;    private Map<String, Subscription> subscriptionMap;    @Autowired    public SubscriptionServiceImpl(RestTemplate restTemplate) {        this.restTemplate = restTemplate;        this.subscriptionMap = new HashMap<>();    }    @Override    public Subscription create(Subscription subscription) {        subscriptionMap.put(subscription.getSubscriptionID(), subscription);        return subscription;    }    @Override    public List<Subscription> findAll() {        return new ArrayList<>(subscriptionMap.values());    }    @Override    public Subscription findById(String subscriptionId) {        return subscriptionMap.get(subscriptionId);    }    @Override    public void update(String subscriptionId, Subscription subscription) {        if (subscriptionMap.containsKey(subscriptionId)) {            subscriptionMap.put(subscriptionId, subscription);        }    }    @Override    public void deleteSubscriptionById(String subscriptionId) {        subscriptionMap.remove(subscriptionId);    }}